import java.io.BufferedReader;import java.io.IOException;import java.io.InputStreamReader;public class Main_2670 {    static int n;    static double dp[];    public static void main(String[] args) throws IOException {        BufferedReader br = new BufferedReader(new InputStreamReader(System.in));        n = Integer.parseInt(br.readLine());        dp = new double[n+1];        for (int i = 1; i <= n; i++) {            dp[i] = Double.parseDouble(br.readLine());        }        double max = dp[0];        for (int i = 1; i <= n; i++) {            dp[i] = Math.max(dp[i], dp[i - 1] * dp[i]);            max = Math.max(max, dp[i]);        }        System.out.printf("%.3f", max);    }}