package boj;import java.io.BufferedReader;import java.io.IOException;import java.io.InputStreamReader;import java.util.ArrayList;import java.util.Collections;import java.util.List;import java.util.StringTokenizer;public class Main_1049 {    public static void main(String[] args) throws IOException {        BufferedReader br = new BufferedReader(new InputStreamReader(System.in));        StringTokenizer st = new StringTokenizer(br.readLine());        int N = Integer.parseInt(st.nextToken());        int M = Integer.parseInt(st.nextToken());        List<Integer> pack = new ArrayList<>();        List<Integer> unit = new ArrayList<>();        for (int i = 0; i < M; i++) {            st = new StringTokenizer(br.readLine());            pack.add(Integer.parseInt(st.nextToken()));            unit.add(Integer.parseInt(st.nextToken()));        }        Collections.sort(pack);        Collections.sort(unit);        int min = Math.min(((N / 6) + 1) * pack.get(0), N * unit.get(0));        min = Math.min(min, ((N / 6)) * pack.get(0) + (N % 6) * unit.get(0));        System.out.println(min);    }}