package boj.all1000.p1200;import java.io.BufferedReader;import java.io.IOException;import java.io.InputStreamReader;public class Main_1213 {    static int alpha[] = new int[27];    public static void main(String[] args) throws IOException {        BufferedReader br = new BufferedReader(new InputStreamReader(System.in));        String input = br.readLine();        StringBuilder result = new StringBuilder();        String answer = "";        for (int i = 0; i < input.length(); i++) {            alpha[input.charAt(i) - 65]++;        }        int flag = 0;        for (int i = 0; i < alpha.length; i++) {            if (alpha[i] % 2 == 1) {                flag++;            }        }        if (flag > 1) {            System.out.println("I'm Sorry Hansoo");            return;        }        for (int i = 0; i < alpha.length; i++) {            for (int j = 0; j < alpha[i] / 2; j++) {                result.append((char) (i + 65));            }        }        answer += result;        String reverse = result.reverse().toString();        result = new StringBuilder();        for (int i = 0; i < alpha.length; i++) {            if (alpha[i] % 2 == 1) {                result.append((char) (i + 65));            }        }        answer = answer + result + reverse;        System.out.println(answer);    }}