package samsung;import java.io.BufferedReader;import java.io.IOException;import java.io.InputStreamReader;public class Permutation {    public static boolean check(String num1, String num2) {        for (int i = 0; i < num1.length(); i++) {            int index = num2.indexOf(num1.charAt(i));            if (index == -1) {                return false;            }            num2 = num2.substring(0, index) + num2.substring(index + 1);        }        return true;    }    public static void main(String[] args) throws IOException {        BufferedReader br = new BufferedReader(new InputStreamReader(System.in));        StringBuilder sb = new StringBuilder();        int T = Integer.parseInt(br.readLine());        for (int tc = 1; tc <= T; tc++) {            String str = br.readLine();            int N = Integer.parseInt(str);            String result = "impossible";            int i = 1;            while (true) {                i++;                int number = N * i;                String numStr = String.valueOf(number);                if (numStr.length() > str.length()) {                    break;                }                if (check(str, numStr)) {                    result = "possible";                    break;                }            }            sb.append("#" + tc + " " + result + "\n");        }        System.out.print(sb);        br.close();    }}